mapper.sql:
1 = 1
 AND F.FUNCTION_ID = #{functionId, jdbcType=INTEGER} 
 AND F.FUNCTION_URL = #{functionUrl, jdbcType=VARCHAR}

mapper.select:
SELECT SF.* FROM SYS_FUNCTION F INNER JOIN SYS_FUNCTION SF ON SF.PARENT_FUNCTION_ID = F.FUNCTION_ID WHERE F.FUNCTION_ID = #{functionId, jdbcType=INTEGER}
AND EXISTS( SELECT 1 FROM SYS_FUNCTION A INNER JOIN SYS_ROLE_FUNCTION RF ON RF.FUNCTION_ID = A.FUNCTION_ID INNER JOIN SYS_USER_ROLE UR ON UR.ROLE_ID = RF.ROLE_ID WHERE A.FUNCTION_ID = SF.FUNCTION_ID AND UR.USER_ID = #{userId, jdbcType=INTEGER} )
ORDER BY F.FUNCTION_LEVEL, F.FUNCTION_ORDER

SELECT SF.* FROM SYS_FUNCTION F INNER JOIN SYS_FUNCTION SF ON SF.PARENT_FUNCTION_ID = F.FUNCTION_ID WHERE F.FUNCTION_ID = #{functionId, jdbcType=INTEGER}
AND EXISTS( SELECT 1 FROM SYS_FUNCTION A INNER JOIN SYS_ROLE_FUNCTION RF ON RF.FUNCTION_ID = A.FUNCTION_ID INNER JOIN SYS_USER_ROLE UR ON UR.ROLE_ID = RF.ROLE_ID INNER JOIN SYS_ROLE R ON R.ROLE_ID = UR.ROLE_ID AND R.IS_VALID = 'Y' WHERE A.FUNCTION_ID = SF.FUNCTION_ID AND UR.USER_ID = #{userId, jdbcType=INTEGER} )
ORDER BY F.FUNCTION_LEVEL, F.FUNCTION_ORDER
